-- the main character handler
local bronann = {};

-- the main map loading code
function Load(m)

	Map = m;
	ObjectManager = Map.object_supervisor;
	DialogueManager = Map.dialogue_supervisor;
	EventManager = Map.event_supervisor;
	TreasureManager = Map.treasure_supervisor;
	GlobalEvents = Map.map_event_group;

	Map.unlimited_stamina = true;

	CreateCharacters();
	CreateObjects();

	-- Set the camera focus on bronann
	Map:SetCamera(bronann);

	CreateEvents();
	CreateZones();

	-- If not done, start the opening dialogue
	if (GlobalEvents:DoesEventExist("opening_dialogue_done") == false) then
		Map:PushState(hoa_map.MapMode.STATE_SCENE);
		EventManager:StartEvent("opening", 1000);
	end

end

-- the map update function handles checks done on each game tick.
function Update()
	-- Check whether the character is in one of the zones
	CheckZones();
end

-- Character creation
function CreateCharacters()
	bronann = _CreateSprite(Map, "Bronann", 23.5, 17.5);
	bronann:SetDirection(hoa_map.MapMode.SOUTH);
	bronann:SetMovementSpeed(hoa_map.MapMode.NORMAL_SPEED);
	bronann:SetNoCollision(false);

	-- set up the position according to the previous map
	if (GlobalManager:GetPreviousLocation() == "from_bronanns_home") then
		bronann:SetPosition(31.5, 17.5);
		bronann:SetDirection(hoa_map.MapMode.WEST);
		bronann:SetContext(hoa_map.MapMode.CONTEXT_02);
	end

	Map:AddGroundObject(bronann);
end

